INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (1, '程家湾老人们的家', '程家湾村中老人们的聚居地，这里依山而建的民居保留着赣东北传统建筑风格。革命年代，村民们曾在此掩护红军伤员、传递情报，为闽浙赣革命根据地的巩固发展作出了重要贡献。如今仍有部分老人在此居住，守护着这片红色土地上的记忆。', '117.88209415', '28.92901258', '171.4256', '居民生活', '', '/uploads/20260213161822_d2d934d3.png', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (2, '碉堡', '程家湾碉堡位于村子制高点，是闽浙赣革命根据地时期红军为抵御外敌侵犯而修建的防御工事。碉堡以石头和泥土堆砖而成，扣眼朗朵清晰可见，充分利用了山地地形优势，是程家湾作为红军战略要地的重要物证。', '117.88216000', '28.92739000', '229.8000', '军事遗址', '', '', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (3, '炮台', '红军炮台遗址位于程家湾山顶附近，居高临下视野开阔。当年红军在此架设火炮阵地，扮演着保卫根据地、掩护部队转移的关键角色。炮台遗址与碜堡遥相呼应，共同构成了程家湾的红军防御体系。', '117.88170000', '28.92695000', '219.5000', '军事遗址', '', '', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (4, '程家湾突围指挥部', '程家湾突围指挥部是闽浙赣省委和红军在程家湾战斗期间的核心指挥中心。当年红军将领在此制定突围作战计划，组织部队实施战略转移。作为重要的军事指挥地点，它见证了红军在困境中英勇不屈的革命精神。', '117.88210821', '28.92943888', '174.8743', '革命旧址', '', '/uploads/20260213161238_1b35346e.png', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (5, '胜利之门', '胜利之门是为纪念程家湾突围战胜利而建造的标志性建筑，位于进入村庄的主通道口。门楼巨大庄严，象征着革命先烈为民族解放事业所取得的伟大胜利，是进入程家湾红色教育基地的第一站。', '117.88075000', '28.92838000', '168.4000', '纪念设施', '', '/uploads/20260213160138_b2f8ac0a.png', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (6, '程家湾广场', '程家湾广场是村中的中心集会区，也是红色教育活动的主要场所。广场周围布局有红色文化展示标识，是参观者集合出发、了解程家湾革命历史的起点。', '117.88225000', '28.92964000', '171.0000', '公共设施', '', '/uploads/20260216123441_7561a453.jpg', 0, '2026-02-16 03:06:26');
INSERT INTO `sites` (`id`, `name`, `description`, `longitude`, `latitude`, `height`, `category`, `icon`, `cover_image`, `sort_order`, `created_at`) VALUES (7, '程家湾红色纪念馆', '程家湾红色纪念馆，集中展示了程家湾地区的革命历史与红色文化遗产。馆内陈列有珍贵的革命文物、图片史料和口述历史资料，是缅怀先烈、传承红色基因的重要教育基地。', '117.88044967', '28.92843615', '175.0251', '纪念设施', '', '/uploads/20260213133748_50216999.jpg', 1, '2026-02-16 03:06:26');

